/* eslint-disable @angular-eslint/component-selector */
import { Component, OnDestroy, OnInit } from '@angular/core';
import { VehiclesService } from '../../services/vehicles.service'
import { FiltersService } from '../../services/filters.service'
import { Vehicle } from '../../models/Vehicle';
import { PriceRange } from '../../models/PriceRange';
import { SortingType } from '../../models/SortingType';
import { Carbody } from '../../models/Carbody';
import { Fuel } from '../../models/Fuel';
import { TransmissionType } from '../../models/TransmissionType';
import { ReplaySubject, forkJoin, map, takeUntil } from 'rxjs';
import { LoadingSpinnerService } from '../../services/loading-spinner.service';

@Component({
  selector: 'fleet',
  templateUrl: './fleet.component.html',
  styleUrls: ['./fleet.component.css'],
})
export class FleetComponent implements OnInit, OnDestroy {
  public mainBanner = 'assets/images/dope-cars-banner.png';

  public vehicles: Vehicle[] = [];
  public priceRanges: PriceRange[] = [];
  public sortingTypes: SortingType[] = [];
  public carbodies: Carbody[] = [];
  public fuels: Fuel[] = [];
  public transmissionTypes: TransmissionType[] = [];

  private onDestroy$: ReplaySubject<boolean> = new ReplaySubject<boolean>(1);

  public ngOnDestroy(): void {
    this.onDestroy$.next(true);
    this.onDestroy$.complete();
    this.loadingSpinnerService.changeSpinnerState(false);
    console.log("ngOnDestroy - fleet");
  }

  constructor(private vehiclesService: VehiclesService, private getFiltersService: FiltersService, private loadingSpinnerService: LoadingSpinnerService)
  {}

  ngOnInit() {
    forkJoin([
      this.getVehicles(),
      this.getPriceRanges(),
      this.getSortingTypes(),
      this.getTransmissionTypes(),
      this.getCarbodies(),
      this.getFuels(),
      this.getTransmissionTypes()
    ])
      .pipe(takeUntil(this.onDestroy$))
      .subscribe(
        {
          next: () => {
            this.loadingSpinnerService.changeSpinnerState(true);
          },
          error: (err) => { console.log(err) },
        }
      )
  }

  private getVehicles() {
    return this.vehiclesService.getVehicles()
      .pipe(map(r => this.vehicles = r));
  }
  private getPriceRanges() {
    return this.getFiltersService.getPriceRanges()
      .pipe(map(r => this.priceRanges = r));
  }
  private getSortingTypes() {
    return this.getFiltersService.getSortingTypes()
      .pipe(map(r => this.sortingTypes = r));
  }
  private getCarbodies() {
    return this.getFiltersService.getCarbodies()
      .pipe(map(r => this.carbodies = r));
  }
  private getFuels() {
    return this.getFiltersService.getFuels()
      .pipe(map(r => this.fuels = r));
  }
  private getTransmissionTypes() {
    return this.getFiltersService.getTransmissionTypes()
      .pipe(map(r => this.transmissionTypes = r));
  }
}


<section class="container-fluid mt-5">
  <div class="row justify-content-center">
    <div class="col-8">
      <div class="row justify-content-center">
        <div class="col-3">
          <div class="row">
            <div class="col-12">
              <div class="row">
                <div class="col-6 align-content-start">
                  <p class="h5">Filter</p>
                </div>
                <div class="col-6 align-content-end">
                  <button type="button" class="btn btn-link ms-auto me-0">Wyczyść wszystkie filtry</button>
                </div>
              </div>
            </div>
            <hr />
            <div class="col-12">
              <div class="row">
                <div class="col">
                  <p class="h5">Sortuj</p>
                  <select class="form-select" aria-label="Default select example">
                    <option selected>Wybierz typ</option>
                    <option *ngFor="let sort of sortingTypes; let i = index" [value]="i+1">{{sort.typeName}}</option>
                  </select>
                </div>
              </div>
            </div>
            <hr />
            <div class="col-12">
              <div class="row">
                <h5>Marka/Model</h5>
                <div class="col-12">
                  <label for="BrandDataList" class="form-label">Brand</label>
                  <input class="form-control" list="BrandDatalistOptions" id="BrandDataList" placeholder="Brand">
                  <datalist id="BrandDatalistOptions">
                  </datalist>
                </div>
                <div class="col-12">
                  <label for="ModelDataList" class="form-label">Model</label>
                  <input class="form-control" list="ModelDatalistOptions" id="ModelDataList" placeholder="Model">
                  <datalist id="ModelDatalistOptions">
                  </datalist>
                </div>
              </div>
            </div>
            <hr />
            <div class="col-12">
              <h5>Rodzaj Paliwa</h5>
              <div class="row">
                <div class="col m-1" *ngFor="let fuel of fuels">
                  <input type="checkbox" class="btn-check" id="{{fuel.fuelName}}Btn" autocomplete="off">
                  <label class="btn btn-outline-primary" for="{{fuel.fuelName}}Btn">{{fuel.fuelName}}</label>
                </div>
              </div>
            </div>
            <hr />
            <div class="col-12">
              <h5>Cena Za Dzień</h5>
              <div class="row">
                <div class="col-12 m-1" *ngFor="let priceRange of priceRanges">
                  <input type="checkbox" class="btn-check" id="Range{{priceRange.priceRangeId}}Btn" autocomplete="off">
                  <label class="btn btn-outline-primary" for="Range{{priceRange.priceRangeId}}Btn">{{priceRange.priceRangeMin}}-{{priceRange.priceRangeMax}}</label>
                </div>
              </div>
            </div>
            <hr />
            <div class="col-12">
              <h5>Typ Nadwozia</h5>
              <div class="row">
                <div class="col-12 m-1" *ngFor="let carbody of carbodies">
                  <input type="checkbox" class="btn-check" id="{{carbody.carbodyName}}" autocomplete="off">
                  <label class="btn btn-outline-primary" for="{{carbody.carbodyName}}">{{carbody.carbodyName}}</label>
                </div>
              </div>
            </div>
            <hr />
            <div class="col-12">
              <h5>Skrzynia Biegów</h5>
              <div class="row">
                <div class="col-12 m-1" *ngFor="let transmission of transmissionTypes">
                  <input type="checkbox" class="btn-check" id="{{transmission.typeName}}" autocomplete="off">
                  <label class="btn btn-outline-primary" for="{{transmission.typeName}}">{{transmission.typeName}}</label>
                </div>
              </div>
            </div>
            <hr />
          </div>
        </div>
        <div class="col-9">
          <div class="row">
              <div class="col-12">
                <img [src]="mainBanner" class="w-100" alt="main banner" />
              </div>
          </div>
          <div class="row" *ngFor="let vehicle of data">
            <div class="col-12 card mb-3">
              <div class="row">
                <!--<div class="col-3">
                  <img [src]="vehicle.imgURL" class="card-img-top" alt="Photo of the car">
                </div>-->
                <!--<div class="col-9">
                  <div class="row">
                    <div class="col-12">
                      <h5 class="card-title">{{vehicle.brand.brandName}} {{vehicle.model}}</h5>
                    </div>
                    <div class="col-12 mt-2">
                      <h6 class="card-title">{{vehicle.yearOfProduction}} {{vehicle.mileage}} {{vehicle.fuelType}}</h6>
                    </div>
                    <div class="col-12 mt-2">
                      <h6 class="card-title">{{vehicle.doorsAmount}} {{vehicle.carBodyType}}</h6>
                    </div>
                  </div>
                </div>-->
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</section>
