/* eslint-disable @angular-eslint/component-selector */
/* eslint-disable no-debugger */
import { Component, OnDestroy, OnInit } from '@angular/core';
import { VehiclesService } from '../../services/vehicles.service';
import { EquipmentService } from '../../services/equipment.service';
import { FiltersService } from '../../services/filters.service';
import { LoadingSpinnerService } from '../../services/loading-spinner.service';
import { AddCarInterface } from '../../interfaces/add-car.interface';
import { Vehicle } from '../../models/Vehicle';
import { EquipmentElement } from '../../models/EquipmentElement';
import { FormBuilder, FormGroup, Validators, ValidationErrors, FormControl } from '@angular/forms';
import { ReplaySubject, takeUntil, map, forkJoin } from 'rxjs';
import { Brand } from '../../models/Brand';
import { Model } from '../../models/Model';
import { Fuel } from '../../models/Fuel';
import { Carbody } from '../../models/Carbody';
import { TransmissionType } from '../../models/TransmissionType';
import { OperationalEquipment } from '../../interfaces/operational-equipment.interface';
import { YearOfProduction } from '../../models/YearOfProduction';
import { createAtLeastOneValidator } from '../../custom-validators/at-least-one';

@Component({
  selector: 'new-car',
  templateUrl: './new-car.component.html',
  styleUrls: ['./new-car.component.css'],
})
export class NewCarComponent implements OnInit, OnDestroy {
  private isExpanded = false;
  public addCarForm: FormGroup = new FormGroup({});

  private equipment: EquipmentElement[] = []; //Whole equipment elements
  public operationalEquipment: OperationalEquipment[] = []; //Elements which are available in the car
  public vehicleImages: File[] = []; //Photos of the car

  public choosenFuels: string[] = [];

  public brands: Brand[] = [];
  public models: Model[] = [];
  public fuels: Fuel[] = [];
  public carbodies: Carbody[] = [];
  public transmissionTypes: TransmissionType[] = [];
  public years: YearOfProduction[] = [];

  private onDestroy$: ReplaySubject<boolean> = new ReplaySubject<boolean>(1);

  public optionsHidden = true;

  public areFuelsLoaded = false;

  public submitClicked = false;

  onSubmit() {
    this.submitClicked = true;
  }

  displaySelectOptions() {
    (this.optionsHidden) ? this.optionsHidden = false : this.optionsHidden = true;
  }

  changeFuelsState() {
    this.areFuelsLoaded = true;
  }

  updateChoosenFuels(formControlName: string) {
    if (this.addCarForm.get('fuels')!.get(formControlName)!.value) {
      this.choosenFuels.push(formControlName);
    } else {
      const index = this.choosenFuels.indexOf(formControlName, 0);
      if (index > -1) {
        this.choosenFuels.splice(index, 1);
      }
    }
  }

  updateValue(opElement: OperationalEquipment) {
    if (opElement.isChecked === false) {
      opElement.isChecked = true;
    } else {
      opElement.isChecked = false;
    }
 }

  constructor(private addCarService: VehiclesService, private getEquipmentElementsService: EquipmentService,
    private formBuilder: FormBuilder, private getFiltersService: FiltersService, private loadingSpinnerService: LoadingSpinnerService) {}

  public ngOnDestroy(): void {
    this.onDestroy$.next(true);
    this.onDestroy$.complete();
    this.loadingSpinnerService.changeSpinnerState(false);
    console.log("ngOnDestroy - newCar");
  }

  ngOnInit() {
    this.initForm();
    forkJoin([
      this.getEquipmentElements(),
      this.getCarbodies(),
      this.getFuels(),
      this.getTransmissionTypes(),
      this.getModels(),
      this.getBrands(),
      this.getYears()
    ])
      .pipe(takeUntil(this.onDestroy$))
      .subscribe(
         {
          next: r => {
             console.log("result", r);
             this.loadingSpinnerService.changeSpinnerState(true);
             this.initFuelOptions();
             this.changeFuelsState();
          },
           error: err => { console.log("error", err); },
         }
      )
  }

  initForm() {
    this.addCarForm = this.formBuilder.group({
      brand: ['', Validators.required, ],
      model: ['', Validators.required],
      yearOfProduction: ['', Validators.required],
      mileage: ['', Validators.required],
      carbodyType: ['', Validators.required],
      transmissionType: ['', Validators.required],
      vehicleImage: ['', Validators.required]
    });
    this.addCarForm.addControl('fuelsOptions', new FormGroup({}));
    this.addCarForm.get('fuelsOptions')!.addValidators(createAtLeastOneValidator());
  }

  initFuelOptions() {
    const fuelsTemp: FormGroup = this.addCarForm.controls.fuelsOptions as FormGroup;
    for (const fuel of this.fuels) {
      console.log(fuel.fuelName);
      fuelsTemp.addControl(fuel.fuelName, new FormControl(false));
    }
  }
 
  fileChanged(event: Event) {
    const imageInputElement = event.target as HTMLInputElement;
    if (imageInputElement.files && imageInputElement.files.length > 0) {
      const files = imageInputElement.files;
      for (const file of files) {
        this.vehicleImages.push(file);
      }
    }
  }

  submitForm() {
    if (this.addCarForm.valid) {
      const formData: AddCarInterface = this.addCarForm.value;
      const newVehicle: Vehicle = new Vehicle();

      newVehicle.brandId = this.brands.find(brand => brand.brandName === formData.brand)!.brandId!;
      newVehicle.modelId = this.models.find(model => model.modelName === formData.model)!.modelId!;

      newVehicle.yearOfProductionId = this.years.find(year => year.year === formData.yearOfProduction)!.yearId!;
      newVehicle.mileage = formData.mileage;

      formData.fuels.forEach(f1 => {
        const matchingFuel = this.fuels.find(f2 => f2.fuelName === f1);
        if (matchingFuel) {
          newVehicle.fuels.push(matchingFuel);
        }
      })

      newVehicle.carbodyId = this.carbodies.find(c => c.carbodyName === formData.carBodyType)!.carbodyId!;

      for (const opElement of this.operationalEquipment) {
        if (opElement.isChecked === true) {
          const matchingEquipmentElement = this.equipment.find(ee => ee.elementId === opElement.eqElement.elementId);
          if (matchingEquipmentElement) {
            newVehicle.equipment.push(matchingEquipmentElement);
          }
        }
      }
      this.addCarService.addVehicle(newVehicle)
        .pipe(takeUntil(this.onDestroy$))
        .subscribe(
          {
            next: r => { debugger },
            error: err => { console.log("błąd"), console.log(err) }
          });
    }
    else {
      console.log("Form is not valid!!!");
      this.getFormValidationErrors();
    } 
  }

  getFormValidationErrors() {
    Object.keys(this.addCarForm.controls).forEach((key,value) => {
      const controlErrors: ValidationErrors = this.addCarForm.get(key)!.errors!;
      Object.keys(controlErrors || {}).forEach(keyError => {
        console.log(`Key control: ${key}, keyError: ${keyError}, errValue: ${controlErrors[keyError]}`);
      });
    });
  }

  private getEquipmentElements() {
    return this.getEquipmentElementsService.getEquipmentElements()
      .pipe(map(r => this.equipment = r));
  }
  private getCarbodies() {
    return this.getFiltersService.getCarbodies()
      .pipe(map(r => this.carbodies = r));
  }
  private getFuels() {
    return this.getFiltersService.getFuels()
      .pipe(map(r => this.fuels = r));
  }
  private getTransmissionTypes() {
    return this.getFiltersService.getTransmissionTypes()
      .pipe(map(r => this.transmissionTypes = r));
  }
  private getModels() {
    return this.getFiltersService.getModels()
      .pipe(map(r => this.models = r));
  }
  private getBrands() {
    return this.getFiltersService.getBrands()
      .pipe(map(r => this.brands = r));
  }
  private getYears() {
    return this.getFiltersService.getYears()
      .pipe(map(r => this.years = r));
  }

  collapse() {
    this.isExpanded = false;
  }

  toggle() {
    this.isExpanded = !this.isExpanded;
  }
}
<section class="mt-5 mb-5">
  <form [formGroup]="addCarForm" (ngSubmit)="submitForm()">
    <div class="container-fluid">
      <div class="row justify-content-center">
        <div class="col-8 mb-3">
          <label for="BrandDataList" class="form-label">Marka</label>
          <input formControlName="brand" class="form-control" list="datalistOptions" id="BrandDataList" placeholder="Marka">
          <datalist id="BrandDatalistOptions">
            <option *ngFor="let brand of brands" [value]="brand.brandName"></option>
          </datalist>
          <div class="field-message" *ngIf="submitClicked && addCarForm.get('brand')?.hasError('required')">
            Marka jest wymagana.
          </div>
        </div>
        <div class="col-8 mb-3">
          <label for="ModelDataList" class="form-label">Model</label>
          <input formControlName="model" class="form-control" list="datalistOptions" id="ModelDataList" placeholder="Model">
          <datalist id="ModelDatalistOptions">
            <option *ngFor="let model of models" [value]="model.modelName"></option>
          </datalist>
          <div class="field-message" *ngIf="submitClicked && addCarForm.get('model')?.hasError('required')">
            Model jest wymagany.
          </div>
        </div>
        <div class="col-8 mb-3">
          <label for="YearOfProdDataList" class="form-label">Rok Produkcji</label>
          <input formControlName="yearOfProduction" class="form-control" list="datalistOptions" id="YearOfProdDataList" placeholder="Rok Produkcji">
          <datalist id="YearOfProdDatalistOptions">
            <option *ngFor="let year of years" [value]="year.year"></option>
          </datalist>
          <div class="field-message" *ngIf="submitClicked && addCarForm.get('yearOfProduction')?.hasError('required')">
            Rok produkcji jest wymagany.
          </div>
        </div>
        <div class="col-8 mb-3">
          <div class="input-group">
            <label for="mileageInput" class="form-label w-100">Przebieg</label>
            <input formControlName="mileage" id="mileageInput" type="text" class="form-control" placeholder="Przebieg">
          </div>
          <div class="field-message" *ngIf="submitClicked && addCarForm.get('mileage')?.hasError('required')">
            Przebieg jest wymagany.
          </div>
        </div>
        <div class="col-8 mb-3 position-relative">
          <label for="customSelect" class="form-label">Rodzaj paliwa</label>
          <div class="customSelect" id="customSelect" (click)="displaySelectOptions()" tabindex="0">
            <ng-container *ngIf="choosenFuels.length > 0; else elseBlock">
              <span *ngFor="let fuel of choosenFuels">
                {{fuel}}
              </span>
            </ng-container>
            <ng-template #elseBlock>Wybierz rodzaj paliwa</ng-template>
          </div>
          <div class="options position-absolute w-auto" formGroupName="fuelsOptions" *ngIf="areFuelsLoaded && !optionsHidden">
            <div class="option" *ngFor="let fuel of fuels">
              <input type="checkbox" id="{{fuel.fuelName}}" name="{{fuel.fuelName}}" formControlName="{{fuel.fuelName}}" (change)="updateChoosenFuels(fuel.fuelName)" />
              <label class="label-box" for="{{fuel.fuelName}}">{{fuel.fuelName}}</label>
            </div>
            <div class="field-message in-select" *ngIf="submitClicked && addCarForm.get('fuelsOptions')?.hasError('atLeastOne')">
              Co najmniej 1 typ paliwa jest wymagany.
            </div>
          </div>
          <div class="field-message" *ngIf="optionsHidden && submitClicked && addCarForm.get('fuelsOptions')?.hasError('atLeastOne')">
            Co najmniej 1 typ paliwa jest wymagany.
          </div>
        </div>
        <div class="col-8 mb-3">
          <label for="TransmissionTypeDataList" class="form-label">Rodzaj skrzyni biegów</label>
          <input formControlName="transmissionType" class="form-control" list="datalistOptions" id="TransmissionTypeDataList" placeholder="Rodzaj skrzyni biegów">
          <datalist id="TransmissionTypeDatalistOptions">
            <option *ngFor="let transmissionType of transmissionTypes" [value]="transmissionType.typeName"></option>
          </datalist>
          <div class="field-message" *ngIf="submitClicked && addCarForm.get('transmissionType')?.hasError('required')">
            Rodzaj skrzyni biegów jest wymagany.
          </div>
        </div>
        <div class="col-8 mb-3">
          <label for="CarbodyDataList" class="form-label">Rodzaj nadwozia</label>
          <input formControlName="carbodyType" class="form-control" list="datalistOptions" id="CarbodyDataList" placeholder="Rodzaj nadwozia">
          <datalist id="CarbodyDatalistOptions">
            <option *ngFor="let carbody of carbodies" [value]="carbody.carbodyName"></option>
          </datalist>
          <div class="field-message" *ngIf="submitClicked && addCarForm.get('carbodyType')?.hasError('required')">
            Rodzaj nadwozia jest wymagany.
          </div>
        </div>
        <div class="col-8 mb-3">
          <label for="vehicleImage" class="form-label">Zdjęcia pojazdu</label>
          <input formControlName="vehicleImage" class="form-control" id="vehicleImage" type="file" (change)="fileChanged($event)" multiple>
          <div class="field-message" *ngIf="submitClicked && addCarForm.get('vehicleImage')?.hasError('required')">
            Zdjęcia pojazdu są wymagane(min. 1).
          </div>
        </div>
        <div class="col-8 mb-5">
          <div class="row">
            <div class="col-4 mb-2 d-flex justify-content-center" *ngFor="let opElement of operationalEquipment">
              <input type="checkbox" class="btn-check" id="{{opElement.eqElement.elementName}}" autocomplete="off" (click)="updateValue(opElement)">
              <label class="btn btn-outline-primary" for="{{opElement.eqElement.elementName}}">{{opElement.eqElement.elementName}}</label>
            </div>
          </div>
        </div>
        <div class="col-8 d-flex justify-content-center">
          <button type="submit" class="btn btn-primary w-auto submitBtn" (click)="onSubmit()">Dodaj pojazd</button>
        </div>
      </div>
    </div>
  </form>
</section>
