import { AbstractControl, FormGroup, ValidationErrors, ValidatorFn } from '@angular/forms';
import { Brand } from '../models/Brand';

export function createIsBrandChosenValidator(brands: Brand[], isModelBlocked: any): ValidatorFn {
  return (form: AbstractControl): ValidationErrors | null => {
    console.log(form.value);
    console.log(isModelBlocked.decision);
    if (brands.includes(form.value)) {
      isModelBlocked.decision = false;
      return null;
    }
    return { isBrandChosen: true };
  }
}
