/* eslint-disable @typescript-eslint/no-non-null-assertion */
/* eslint-disable @angular-eslint/component-selector */
import { Component, HostListener, OnDestroy, OnInit } from '@angular/core';
import { VehiclesService } from '../../services/vehicles.service'
import { FiltersService } from '../../services/filters.service'
import { Vehicle } from '../../models/Vehicle';
import { PriceRange } from '../../models/PriceRange';
import { SortingType } from '../../models/SortingType';
import { Carbody } from '../../models/Carbody';
import { Fuel } from '../../models/Fuel';
import { TransmissionType } from '../../models/TransmissionType';
import { ReplaySubject, forkJoin, map, takeUntil } from 'rxjs';
import { LoadingSpinnerService } from '../../services/loading-spinner.service';
import { Model } from '../../models/Model';
import { Brand } from '../../models/Brand';
import { FormControl, FormGroup } from '@angular/forms';

@Component({
  selector: 'fleet',
  templateUrl: './fleet.component.html',
  styleUrls: ['./fleet.component.css'],
})
export class FleetComponent implements OnInit, OnDestroy {
  public mainBanner = 'assets/images/dope-cars-banner.png';

  public customSelects: FormGroup = new FormGroup({});

  public vehicles: Vehicle[] = [];
  public priceRanges: PriceRange[] = [];
  public sortingTypes: SortingType[] = [];
  public carbodies: Carbody[] = [];
  public fuels: Fuel[] = [];
  public transmissionTypes: TransmissionType[] = [];

  public brands: Brand[] = [];
  public models: Model[] = [];

  public fuelOptionsHidden = true;
  public brandOptionsHidden = true;
  public modelOptionsHidden = true;
  
  public specificModels: Model[] = [];

  public chosenFuels: string[] = [];
  public chosenBrands: string[] = [];
  public chosenModels: string[] = [];

  public areFuelsLoaded = false;
  public areBrandsLoaded = false;

  public brandSearchTerm = "";
  public modelSearchTerm = "";

  private onDestroy$: ReplaySubject<boolean> = new ReplaySubject<boolean>(1);

  @HostListener('document:click', ['$event'])
  onClickFuels(event: MouseEvent) {
    const targetElement = event.target as HTMLElement;

    if (!targetElement.closest('.customFuelSelectAlias')) {
      this.fuelOptionsHidden = true;
    }
    if (!targetElement.closest('.customBrandSelectAlias')) {
      this.brandOptionsHidden = true;
    }
    if (!targetElement.closest('.customModelSelectAlias')) {
      this.modelOptionsHidden = true;
    }
  }

  updateChoosenBrandsOrModels(dataType: string, choice: string) {
    if (this.checkOrUncheckBrandsOrModels(dataType, choice)) {
      if (dataType === 'brand') {
        this.chosenBrands.push(choice);
      } else {
        this.chosenModels.push(choice);
      }
    } else {
      if (dataType === 'brand') {
        const index = this.chosenBrands.indexOf(choice, 0);
        if (index > -1) {
          this.chosenBrands.splice(index, 1);
        }
      } else {
        const index = this.chosenModels.indexOf(choice, 0);
        if (index > -1) {
          this.chosenModels.splice(index, 1);
        }
      }
    }
  }

  checkOrUncheckBrandsOrModels(dataType: string, choice: string) {
    let control;

    if (dataType === "brand") {
      control = this.customSelects.get("brandOptions")!.get(choice)!;
    } else {
      control = this.customSelects.get("modelOptions")!.get(choice)!;
    }

    if (control.value) {
      return true; //zostala teraz zaznaczona
    }
    return false; //zostala teraz odznaczona
  }

  public ngOnDestroy(): void {
    this.onDestroy$.next(true);
    this.onDestroy$.complete();
    this.loadingSpinnerService.changeSpinnerState(false);
    console.log("ngOnDestroy - fleet");
  }

  displayFuelSelectOptions() {
    (this.fuelOptionsHidden) ? this.fuelOptionsHidden = false : this.fuelOptionsHidden = true;
  }

  displayBrandSelectOptions() {
    (this.brandOptionsHidden) ? this.brandOptionsHidden = false : this.brandOptionsHidden = true;
  }

  displayModelSelectOptions() {
    (this.modelOptionsHidden) ? this.modelOptionsHidden = false : this.modelOptionsHidden = true;
  }

  changeFuelsState() {
    this.areFuelsLoaded = true;
  }

  changeBrandsState() {
    this.areBrandsLoaded = true;
  }

  constructor(private vehiclesService: VehiclesService, private getFiltersService: FiltersService, private loadingSpinnerService: LoadingSpinnerService)
  {}

  ngOnInit() {
    this.initForm();
    forkJoin([
      this.getVehicles(),
      this.getPriceRanges(),
      this.getSortingTypes(),
      this.getTransmissionTypes(),
      this.getCarbodies(),
      this.getFuels(),
      this.getTransmissionTypes(),
      this.getModels(),
      this.getBrands()
       
    ])
      .pipe(takeUntil(this.onDestroy$))
      .subscribe(
        {
          next: () => {
            this.loadingSpinnerService.changeSpinnerState(true);
            this.initFuelOptions();
            this.initBrandOptions();
            this.initModelOptions();
            this.changeFuelsState();
            this.changeBrandsState();
          },
          error: (err) => { console.log(err) },
        }
      )
  }

  filterBrands() {
    if (this.brandSearchTerm === "") {
      return this.brands;
    }
    return this.brands.filter(brand => brand.brandName.toLowerCase().startsWith(this.brandSearchTerm.toLowerCase()));
  }

  filterModels() {
    if (this.modelSearchTerm === "") {
      return this.specificModels;
    }
    return this.specificModels.filter(model => model.modelName.toLowerCase().startsWith(this.modelSearchTerm.toLowerCase()));
  }

  initForm() {
    this.customSelects.addControl('fuelOptions', new FormGroup({}));
    this.customSelects.addControl('brandOptions', new FormGroup({}));
    this.customSelects.addControl('modelOptions', new FormGroup({}));
  }

  initFuelOptions() {
    const fuelsTemp: FormGroup = this.customSelects.get("fuelOptions") as FormGroup;
    for (const fuel of this.fuels) {
      fuelsTemp.addControl(fuel.fuelName, new FormControl(false));
    }
  }

  initBrandOptions() {
    const brandsTemp: FormGroup = this.customSelects.get("brandOptions") as FormGroup;
    for (const brand of this.brands) {
      brandsTemp.addControl(brand.brandName, new FormControl(false));
    }
  }

  initModelOptions() {
    const modelsTemp: FormGroup = this.customSelects.get("modelOptions") as FormGroup;
    for (const model of this.models) {
      modelsTemp.addControl(model.modelName, new FormControl(false));
    }
  }

  private getVehicles() {
    return this.vehiclesService.getVehicles()
      .pipe(map(r => this.vehicles = r));
  }
  private getPriceRanges() {
    return this.getFiltersService.getPriceRanges()
      .pipe(map(r => this.priceRanges = r));
  }
  private getSortingTypes() {
    return this.getFiltersService.getSortingTypes()
      .pipe(map(r => this.sortingTypes = r));
  }
  private getCarbodies() {
    return this.getFiltersService.getCarbodies()
      .pipe(map(r => this.carbodies = r));
  }
  private getFuels() {
    return this.getFiltersService.getFuels()
      .pipe(map(r => this.fuels = r));
  }
  private getTransmissionTypes() {
    return this.getFiltersService.getTransmissionTypes()
      .pipe(map(r => this.transmissionTypes = r));
  }
  private getModels() {
    return this.getFiltersService.getModels()
      .pipe(map(r => this.models = r));
  }
  private getBrands() {
    return this.getFiltersService.getBrands()
      .pipe(map(r => this.brands = r));
  }
}


<section class="container-fluid mt-5">
  <div class="row justify-content-center">
    <div class="col-8">
      <div class="row justify-content-center">
        <div class="col-3">
          <div class="row">
            <div class="col-12">
              <div class="row">
                <div class="col-6 align-content-start">
                  <p class="h5">Filter</p>
                </div>
                <div class="col-6 align-content-end">
                  <button type="button" class="btn btn-link ms-auto me-0">Wyczyść wszystkie filtry</button>
                </div>
              </div>
            </div>
            <hr />
            <div class="col-12">
              <div class="row">
                <div class="col">
                  <p class="h5">Sortuj</p>
                  <select class="form-select">
                    <option selected>Wybierz typ</option>
                    <option *ngFor="let sort of sortingTypes; let i = index" [value]="i+1">{{sort.typeName}}</option>
                  </select>
                </div>
              </div>
            </div>
            <hr />
            <div class="col-12">
              <div class="row">
                <p class="h5">Marka/Model</p>
                <div class="col-12 mb-3 position-relative customBrandSelectAlias customBrandSelect">
                  <label for="customBrandSelect" class="form-label">Marka pojazdu</label>
                  <input [(ngModel)]="brandSearchTerm" [ngModelOptions]="{ standalone: true }" type="text" class="form-control" placeholder="Marka pojazdu" (click)="displayBrandSelectOptions()">
                  <div class="options position-absolute w-auto" formGroupName="brandOptions" *ngIf="areBrandsLoaded && !brandOptionsHidden">
                    <div class="option d-flex" *ngFor="let brand of filterBrands()">
                      <input type="checkbox" id="{{brand.brandName}}" name="{{brand.brandName}}" formControlName="{{brand.brandName}}" (change)="updateChoice('brand', brand.brandName)" />
                      <label class="label-box flex-grow-1" for="{{brand.brandName}}">{{brand.brandName}}</label>
                    </div>
                  </div>
                  <div class="field-message" *ngIf="submitClicked && addCarForm.get('brandOptions')?.hasError('atLeastOne')">
                    Marka pojazdu jest wymagana.
                  </div>
                </div>
                <div class="col-12 mb-3 position-relative customModelSelectAlias customModelSelect">
                  <label for="customModelSelect" class="form-label">Marka pojazdu</label>
                  <input [(ngModel)]="modelSearchTerm" [ngModelOptions]="{ standalone: true }" type="text" class="form-control" placeholder="Model pojazdu" (click)="displayModelSelectOptions()">
                  <div class="options position-absolute w-auto" formGroupName="modelOptions" *ngIf="!modelOptionsHidden">
                    <div class="option d-flex" *ngFor="let model of filterModels()">
                      <input type="checkbox" id="{{model.modelName}}" name="{{model.modelName}}" formControlName="{{model.modelName}}" (change)="updateChoice('model', model.modelName)" />
                      <label class="label-box flex-grow-1" for="{{model.modelName}}">{{model.modelName}}</label>
                    </div>
                  </div>
                  <div class="field-message" *ngIf="submitClicked && addCarForm.get('modelOptions')?.hasError('atLeastOne')">
                    Model pojazdu jest wymagany.
                  </div>
                </div>
              </div>
            </div>
            <hr />
            <div class="col-12">
              <p class="h5">Rodzaj Paliwa</p>
              <div class="row">
                <div class="col m-1" *ngFor="let fuel of fuels">
                  <input type="checkbox" class="btn-check" id="{{fuel.fuelName}}Btn" autocomplete="off">
                  <label class="btn btn-outline-primary" for="{{fuel.fuelName}}Btn">{{fuel.fuelName}}</label>
                </div>
              </div>
            </div>
            <hr />
            <div class="col-12">
              <p class="h5">Cena Za Dzień</p>
              <div class="row">
                <div class="col-12 m-1" *ngFor="let priceRange of priceRanges">
                  <input type="checkbox" class="btn-check" id="Range{{priceRange.priceRangeId}}Btn" autocomplete="off">
                  <label class="btn btn-outline-primary" for="Range{{priceRange.priceRangeId}}Btn">{{priceRange.priceRangeMin}}-{{priceRange.priceRangeMax}}</label>
                </div>
              </div>
            </div>
            <hr />
            <div class="col-12">
              <p class="h5">Typ Nadwozia</p>
              <div class="row">
                <div class="col-12 m-1" *ngFor="let carbody of carbodies">
                  <input type="checkbox" class="btn-check" id="{{carbody.carbodyName}}" autocomplete="off">
                  <label class="btn btn-outline-primary" for="{{carbody.carbodyName}}">{{carbody.carbodyName}}</label>
                </div>
              </div>
            </div>
            <hr />
            <div class="col-12">
              <p class="h5">Skrzynia Biegów</p>
              <div class="row">
                <div class="col-12 m-1" *ngFor="let transmission of transmissionTypes">
                  <input type="checkbox" class="btn-check" id="{{transmission.typeName}}" autocomplete="off">
                  <label class="btn btn-outline-primary" for="{{transmission.typeName}}">{{transmission.typeName}}</label>
                </div>
              </div>
            </div>
            <hr />
          </div>
        </div>
        <div class="col-9">
          <div class="row">
              <div class="col-12">
                <img [src]="mainBanner" class="w-100" alt="main banner" />
              </div>
          </div>
          <div class="row" *ngFor="let vehicle of vehicles">
            <div class="col-12 card mb-3">
              <div class="row">
                <!--<div class="col-3">
                  <img [src]="vehicle.imgURL" class="card-img-top" alt="Photo of the car">
                </div>-->
                <!--<div class="col-9">
                  <div class="row">
                    <div class="col-12">
                      <h5 class="card-title">{{vehicle.brand.brandName}} {{vehicle.model}}</h5>
                    </div>
                    <div class="col-12 mt-2">
                      <h6 class="card-title">{{vehicle.yearOfProduction}} {{vehicle.mileage}} {{vehicle.fuelType}}</h6>
                    </div>
                    <div class="col-12 mt-2">
                      <h6 class="card-title">{{vehicle.doorsAmount}} {{vehicle.carBodyType}}</h6>
                    </div>
                  </div>
                </div>-->
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</section>
