/* eslint-disable @typescript-eslint/no-non-null-assertion */
/* eslint-disable @angular-eslint/component-selector */
/* eslint-disable no-debugger */
import { Component, HostListener, OnDestroy, OnInit } from '@angular/core';
import { VehiclesService } from '../../services/vehicles.service';
import { EquipmentService } from '../../services/equipment.service';
import { FiltersService } from '../../services/filters.service';
import { LoadingSpinnerService } from '../../services/loading-spinner.service';
import { AddCarInterface } from '../../interfaces/add-car.interface';
import { Vehicle } from '../../models/Vehicle';
import { EquipmentElement } from '../../models/EquipmentElement';
import { FormBuilder, FormGroup, Validators, ValidationErrors, FormControl } from '@angular/forms';
import { ReplaySubject, takeUntil, map, forkJoin } from 'rxjs';
import { Brand } from '../../models/Brand';
import { Model } from '../../models/Model';
import { Fuel } from '../../models/Fuel';
import { Carbody } from '../../models/Carbody';
import { TransmissionType } from '../../models/TransmissionType';
import { OperationalEquipment } from '../../interfaces/operational-equipment.interface';
import { YearOfProduction } from '../../models/YearOfProduction';
import { createAtLeastOneValidator } from '../../custom-validators/at-least-one';

@Component({
  selector: 'new-car',
  templateUrl: './new-car.component.html',
  styleUrls: ['./new-car.component.css'],
})
export class NewCarComponent implements OnInit, OnDestroy {
  private isExpanded = false;
  public addCarForm: FormGroup = new FormGroup({});

  private equipment: EquipmentElement[] = []; //Whole equipment elements
  public operationalEquipment: OperationalEquipment[] = []; //Elements which are available in the car
  public vehicleImages: File[] = []; //Photos of the car

  public chosenFuels: string[] = [];

  public brands: Brand[] = [];
  public models: Model[] = [];
  public fuels: Fuel[] = [];
  public carbodies: Carbody[] = [];
  public transmissionTypes: TransmissionType[] = [];
  public years: YearOfProduction[] = [];

  private onDestroy$: ReplaySubject<boolean> = new ReplaySubject<boolean>(1);

  public fuelOptionsHidden = true;
  public brandOptionsHidden = true;
  public modelOptionsHidden = true;

  public specificModels: Model[] = [];

  public areFuelsLoaded = false;
  public areBrandsLoaded = false;

  public submitClicked = false;

  public brandSearchTerm = "";
  public modelSearchTerm = "";

  @HostListener('document:click', ['$event'])
  onClickFuels(event: MouseEvent) {
    const targetElement = event.target as HTMLElement;

    if (!targetElement.closest('.customFuelSelectAlias')) {
      this.fuelOptionsHidden = true;
    }
    if (!targetElement.closest('.customBrandSelectAlias')) {
      this.brandOptionsHidden = true;
    }
    if (!targetElement.closest('.customModelSelectAlias')) {
      this.modelOptionsHidden = true;
    } 
  }

  updateChoice(dataType: string, choice: string) {
    if (this.checkOrUncheck(dataType, choice)) {
      if (dataType === 'brand') {
        const choosenBrandId = this.brands.find(brand => brand.brandName === choice)!.brandId!;
        this.specificModels = this.models.filter(model => model.brandId === choosenBrandId);
        this.brandSearchTerm = choice;
      }
      this.uncheckPreviousChoice(dataType, choice);
    } else {
      if (dataType === "brand") {
        this.specificModels = [];
        (dataType === "brand") ? this.brandSearchTerm = "" : this.modelSearchTerm = "";
      }
    }
  }

  checkOrUncheck(dataType: string, choice: string) {
    let control;

    if (dataType === "brand") {
      control = this.addCarForm.get("brandOptions")!.get(choice)!; 
    } else {
      control = this.addCarForm.get("modelOptions")!.get(choice)!;
    }

    if (control.value) {
      return true; //zostala teraz zaznaczona
    }
    return false; //zostala teraz odznaczona
  }

  uncheckPreviousChoice(dataType: string, choice: string) {
    let tempGroup;

    if (dataType === "brand") {
      tempGroup = this.addCarForm.get("brandOptions") as FormGroup;
    } else {
      tempGroup = this.addCarForm.get("modelOptions") as FormGroup;
    }

    const controls = tempGroup.controls;

    for (const control in controls) {
      if (tempGroup.get(control)!.value === true && control !== choice) {
        tempGroup.get(control)?.setValue(false);
      }
    }
  }

  filterBrands() {
      if (this.brandSearchTerm === "") {
        return this.brands;
      }
      return this.brands.filter(brand => brand.brandName.toLowerCase().startsWith(this.brandSearchTerm.toLowerCase()));
  }

  filterModels() {
    if (this.modelSearchTerm === "") {
      return this.specificModels;
    }
    return this.specificModels.filter(model => model.modelName.toLowerCase().startsWith(this.modelSearchTerm.toLowerCase()));
  }

  onSubmit() {
    this.submitClicked = true;
  }

  displayFuelSelectOptions() {
    (this.fuelOptionsHidden) ? this.fuelOptionsHidden = false : this.fuelOptionsHidden = true;
  }

  displayBrandSelectOptions() {
    (this.brandOptionsHidden) ? this.brandOptionsHidden = false : this.brandOptionsHidden = true;
  }

  displayModelSelectOptions() {
    (this.modelOptionsHidden) ? this.modelOptionsHidden = false : this.modelOptionsHidden = true;
  }

  changeFuelsState() {
    this.areFuelsLoaded = true;
  }

  changeBrandsState() {
    this.areBrandsLoaded = true;
  }

  updateChoosenFuels(formControlName: string) {
    if (this.addCarForm.get('fuelOptions')!.get(formControlName)!.value) {
      this.chosenFuels.push(formControlName);
    } else {
      const index = this.chosenFuels.indexOf(formControlName, 0);
      if (index > -1) {
        this.chosenFuels.splice(index, 1);
      }
    }
  }

  updateValue(opElement: OperationalEquipment) {
    if (opElement.isChecked === false) {
      opElement.isChecked = true;
    } else {
      opElement.isChecked = false;
    }
 }

  constructor(private addCarService: VehiclesService, private getEquipmentElementsService: EquipmentService,
    private formBuilder: FormBuilder, private getFiltersService: FiltersService, private loadingSpinnerService: LoadingSpinnerService) {}

  public ngOnDestroy(): void {
    this.onDestroy$.next(true);
    this.onDestroy$.complete();
    this.loadingSpinnerService.changeSpinnerState(false);
    console.log("ngOnDestroy - newCar");
  }

  ngOnInit() {
    this.initForm();
    forkJoin([
      this.getEquipmentElements(),
      this.getCarbodies(),
      this.getFuels(),
      this.getTransmissionTypes(),
      this.getBrands(),
      this.getModels(),
      this.getYears()
    ])
      .pipe(takeUntil(this.onDestroy$))
      .subscribe(
         {
          next: () => {
             this.loadingSpinnerService.changeSpinnerState(true);
             this.initFuelOptions();
             this.initBrandOptions();
             this.initModelOptions();
             this.changeFuelsState();
             this.changeBrandsState();
          },
           error: err => { console.log("error", err); },
         }
      )
  }

  initForm() {
    this.addCarForm = this.formBuilder.group({
      yearOfProduction: ['', Validators.required],
      mileage: ['', Validators.required],
      carbodyType: ['', Validators.required],
      transmissionType: ['', Validators.required],
      vehicleImage: ['', Validators.required]
    });
    this.addCarForm.addControl('fuelOptions', new FormGroup({}));
    this.addCarForm.get('fuelOptions')!.addValidators(createAtLeastOneValidator());

    this.addCarForm.addControl('brandOptions', new FormGroup({}));
    this.addCarForm.get('brandOptions')!.addValidators(createAtLeastOneValidator());

    this.addCarForm.addControl('modelOptions', new FormGroup({}));
    this.addCarForm.get('modelOptions')!.addValidators(createAtLeastOneValidator());
  }

  initFuelOptions() {
    const fuelsTemp: FormGroup = this.addCarForm.controls.fuelOptions as FormGroup;
    for (const fuel of this.fuels) {
      fuelsTemp.addControl(fuel.fuelName, new FormControl(false));
    }
  }

  initBrandOptions() {
    const brandsTemp: FormGroup = this.addCarForm.controls.brandOptions as FormGroup;
    for (const brand of this.brands) {
      brandsTemp.addControl(brand.brandName, new FormControl(false));
    }
  }

  initModelOptions() {
    const modelsTemp: FormGroup = this.addCarForm.controls.modelOptions as FormGroup;
    for (const model of this.models) {
      modelsTemp.addControl(model.modelName, new FormControl(false));
    }
  }
 
  fileChanged(event: Event) {
    const imageInputElement = event.target as HTMLInputElement;
    if (imageInputElement.files && imageInputElement.files.length > 0) {
      const files = imageInputElement.files;
      for (const file of files) {
        this.vehicleImages.push(file);
      }
    }
  }

  submitForm() {
    if (this.addCarForm.valid) {
      const formData: AddCarInterface = this.addCarForm.value;
      const newVehicle: Vehicle = new Vehicle();

      newVehicle.brandId = this.brands.find(brand => brand.brandName === formData.brand)!.brandId!;
      newVehicle.modelId = this.models.find(model => model.modelName === formData.model)!.modelId!;

      newVehicle.yearOfProductionId = this.years.find(year => year.year === formData.yearOfProduction)!.yearId!;
      newVehicle.mileage = formData.mileage;

      formData.fuels.forEach(f1 => {
        const matchingFuel = this.fuels.find(f2 => f2.fuelName === f1);
        if (matchingFuel) {
          newVehicle.fuels.push(matchingFuel);
        }
      })

      newVehicle.carbodyId = this.carbodies.find(c => c.carbodyName === formData.carBodyType)!.carbodyId!;

      for (const opElement of this.operationalEquipment) {
        if (opElement.isChecked === true) {
          const matchingEquipmentElement = this.equipment.find(ee => ee.elementId === opElement.eqElement.elementId);
          if (matchingEquipmentElement) {
            newVehicle.equipment.push(matchingEquipmentElement);
          }
        }
      }
      this.addCarService.addVehicle(newVehicle)
        .pipe(takeUntil(this.onDestroy$))
        .subscribe(
          {
            next: () => { debugger },
            error: err => { console.log("błąd"), console.log(err) }
          });
    }
    else {
      console.log("Form is not valid!!!");
      this.getFormValidationErrors();
    } 
  }

  getFormValidationErrors() {
    Object.keys(this.addCarForm.controls).forEach((key) => {
      const controlErrors: ValidationErrors = this.addCarForm.get(key)!.errors!;
      Object.keys(controlErrors || {}).forEach(keyError => {
        console.log(`Key control: ${key}, keyError: ${keyError}, errValue: ${controlErrors[keyError]}`);
      });
    });
  }

  private getEquipmentElements() {
    return this.getEquipmentElementsService.getEquipmentElements()
      .pipe(map(r => this.equipment = r));
  }
  private getCarbodies() {
    return this.getFiltersService.getCarbodies()
      .pipe(map(r => this.carbodies = r));
  }
  private getFuels() {
    return this.getFiltersService.getFuels()
      .pipe(map(r => this.fuels = r));
  }
  private getTransmissionTypes() {
    return this.getFiltersService.getTransmissionTypes()
      .pipe(map(r => this.transmissionTypes = r));
  }
  private getModels() {
    return this.getFiltersService.getModels()
      .pipe(map(r => this.models = r));
  }
  private getBrands() {
    return this.getFiltersService.getBrands()
      .pipe(map(r => this.brands = r));
  }
  private getYears() {
    return this.getFiltersService.getYears()
      .pipe(map(r => this.years = r));
  }

  collapse() {
    this.isExpanded = false;
  }

  toggle() {
    this.isExpanded = !this.isExpanded;
  }
}
<section class="pt-3 pb-3">
  <form [formGroup]="addCarForm" (ngSubmit)="submitForm()">
    <div class="container-fluid">
      <div class="row justify-content-center">
        <div class="col-8 mb-3 position-relative customBrandSelectAlias customBrandSelect">
          <label for="customBrandSelect" class="form-label">Marka pojazdu</label>
          <input [(ngModel)]="brandSearchTerm" [ngModelOptions]="{ standalone: true }" type="text" class="form-control" placeholder="Marka pojazdu" (click)="displayBrandSelectOptions()">
          <div class="options position-absolute w-auto" formGroupName="brandOptions" *ngIf="areBrandsLoaded && !brandOptionsHidden">
            <div class="option d-flex" *ngFor="let brand of filterBrands()">
              <input type="checkbox" id="{{brand.brandName}}" name="{{brand.brandName}}" formControlName="{{brand.brandName}}" (change)="updateChoice('brand', brand.brandName)" />
              <label class="label-box flex-grow-1" for="{{brand.brandName}}">{{brand.brandName}}</label>
            </div>
          </div>
          <div class="field-message" *ngIf="submitClicked && addCarForm.get('brandOptions')?.hasError('atLeastOne')">
            Marka pojazdu jest wymagany.
          </div>
        </div>
        <div class="col-8 mb-3 position-relative customModelSelectAlias customModelSelect">
          <label for="customModelSelect" class="form-label">Marka pojazdu</label>
          <input [(ngModel)]="modelSearchTerm" [ngModelOptions]="{ standalone: true }" type="text" class="form-control" placeholder="Model pojazdu" (click)="displayModelSelectOptions()">
          <div class="options position-absolute w-auto" formGroupName="modelOptions" *ngIf="!modelOptionsHidden">
            <div class="option d-flex" *ngFor="let model of filterModels()">
              <input type="checkbox" id="{{model.modelName}}" name="{{model.modelName}}" formControlName="{{model.modelName}}" (change)="updateChoice('model', model.modelName)" />
              <label class="label-box flex-grow-1" for="{{model.modelName}}">{{model.modelName}}</label>
            </div>
          </div>
          <div class="field-message" *ngIf="submitClicked && addCarForm.get('modelOptions')?.hasError('atLeastOne')">
            Model pojazdu jest wymagany.
          </div>
        </div>
        <div class="col-8 mb-3">
          <label for="YearOfProdSelect" class="form-label">Rok produkcji</label>
          <select formControlName="yearOfProduction" class="form-select" id="YearOfProdSelect">
            <option value="" selected>Rok produkcji</option>
            <option *ngFor="let year of years" [value]="year.year">{{year.year}}</option>
          </select>
          <div class="field-message" *ngIf="submitClicked && addCarForm.get('yearOfProduction')?.hasError('required')">
            Rok produkcji jest wymagany.
          </div>
        </div>
        <div class="col-8 mb-3">
          <div class="input-group">
            <label for="mileageInput" class="form-label w-100">Przebieg</label>
            <input formControlName="mileage" id="mileageInput" type="text" class="form-control" placeholder="Przebieg">
          </div>
          <div class="field-message" *ngIf="submitClicked && addCarForm.get('mileage')?.hasError('required')">
            Przebieg jest wymagany.
          </div>
        </div>
        <div class="col-8 mb-3 position-relative customFuelSelectAlias">
          <label for="customSelect" class="form-label">Rodzaj paliwa</label>
          <div class="customSelect" id="customSelect" (click)="displayFuelSelectOptions()" tabindex="0">
            <ng-container *ngIf="chosenFuels.length > 0; else elseBlock">
              <span *ngFor="let fuel of chosenFuels">
                {{fuel}}
              </span>
            </ng-container>
            <ng-template #elseBlock>Wybierz rodzaj paliwa</ng-template>
          </div>
          <div class="options position-absolute w-auto" formGroupName="fuelOptions" *ngIf="areFuelsLoaded && !fuelOptionsHidden">
            <div class="option d-flex" *ngFor="let fuel of fuels">
              <input type="checkbox" id="{{fuel.fuelName}}" name="{{fuel.fuelName}}" formControlName="{{fuel.fuelName}}" (change)="updateChoosenFuels(fuel.fuelName)" />
              <label class="label-box flex-grow-1" for="{{fuel.fuelName}}">{{fuel.fuelName}}</label>
            </div>
          </div>
          <div class="field-message" *ngIf="submitClicked && addCarForm.get('fuelOptions')?.hasError('atLeastOne')">
            Co najmniej 1 typ paliwa jest wymagany.
          </div>
        </div>
        <div class="col-8 mb-3">
          <label for="TransmissionTypeSelect" class="form-label">Rodzaj skrzyni biegów</label>
          <select formControlName="transmissionType" class="form-select" id="TransmissionTypeSelect">
            <option value="" selected>Rodzaj skrzyni biegów</option>
            <option *ngFor="let transmissionType of transmissionTypes" [value]="transmissionType.typeName">{{transmissionType.typeName}}</option>
          </select>
          <div class="field-message" *ngIf="submitClicked && addCarForm.get('transmissionType')?.hasError('required')">
            Rodzaj skrzyni biegów jest wymagany.
          </div>
        </div>
        <div class="col-8 mb-3">
          <label for="CarbodySelect" class="form-label">Rodzaj nadwozia</label>
          <select formControlName="carbodyType" class="form-select" id="CarbodySelect">
            <option value="" selected>Rodzaj nadwozia</option>
            <option *ngFor="let carbody of carbodies" [value]="carbody.carbodyName">{{carbody.carbodyName}}</option>
          </select>
          <div class="field-message" *ngIf="submitClicked && addCarForm.get('carbodyType')?.hasError('required')">
            Rodzaj nadwozia jest wymagany.
          </div>
        </div>
        <div class="col-8 mb-3">
          <label for="vehicleImage" class="form-label">Zdjęcia pojazdu</label>
          <input formControlName="vehicleImage" class="form-control" id="vehicleImage" type="file" (change)="fileChanged($event)" multiple>
          <div class="field-message" *ngIf="submitClicked && addCarForm.get('vehicleImage')?.hasError('required')">
            Zdjęcia pojazdu są wymagane(min. 1).
          </div>
        </div>
        <div class="col-8 mb-5">
          <div class="row">
            <div class="col-4 mb-2 d-flex justify-content-center" *ngFor="let opElement of operationalEquipment">
              <input type="checkbox" class="btn-check" id="{{opElement.eqElement.elementName}}" autocomplete="off" (click)="updateValue(opElement)">
              <label class="btn btn-outline-primary" for="{{opElement.eqElement.elementName}}">{{opElement.eqElement.elementName}}</label>
            </div>
          </div>
        </div>
        <div class="col-8 d-flex justify-content-center">
          <button type="submit" class="btn btn-primary w-auto submitBtn" (click)="onSubmit()">Dodaj pojazd</button>
        </div>
      </div>
    </div>
  </form>
</section>
