import { TransmissionType } from '../../models/TransmissionType';
import { OperationalEquipment } from '../../interfaces/operational-equipment.interface';
import { YearOfProduction } from '../../models/YearOfProduction';

@Component({
  selector: 'new-car',
  templateUrl: './new-car.component.html',
  styleUrls: ['./new-car.component.css'],
})
export class NewCarComponent implements OnInit, OnDestroy {
  private isExpanded = false;
  public addCarForm: FormGroup = new FormGroup({});

  private equipment: EquipmentElement[]; //Whole equipment elements
  public operationalEquipment: OperationalEquipment[]; //Elements which are available in the car
  public vehicleImages: File[] = []; //Photos of the car

  public brands: Brand[];
  public models: Model[];
  public fuels: Fuel[];
  public carbodies: Carbody[];
  public transmissionTypes: TransmissionType[];
  public years: YearOfProduction[];

  private onDestroy$: ReplaySubject<boolean> = new ReplaySubject<boolean>(1);

  public optionsExpanded = false;

  displaySelectOptions() {
    (this.optionsExpanded) ? this.optionsExpanded = false : this.optionsExpanded = true;
  }

  updateValue(opElement: OperationalEquipment) {
    if (opElement.isChecked === false) {
      opElement.isChecked = true;
    } else {
      opElement.isChecked = false;
    }
 }

  constructor(private addCarService: VehiclesService, private getEquipmentElementsService: EquipmentService,
    private formBuilder: FormBuilder, private getFiltersService: FiltersService, private renderer: Renderer2, private elRef: ElementRef) {}

  public ngOnDestroy(): void {
    this.onDestroy$.next(true);
    this.onDestroy$.complete();
  }

  ngOnInit() {
    this.initForm();
    this.getEquipmentElementsService.getEquipmentElements()
      .pipe(takeUntil(this.onDestroy$))
      .subscribe(
        {
          next: r => {
            this.equipment = r;
            this.operationalEquipment = [];
            for (const el of this.equipment) {
              this.operationalEquipment.push({ eqElement: el, isChecked: false });
            }
          },
          error: err => { console.log("error", err); }
      }), 

    this.getFiltersService.getCarbodies()
      .pipe(takeUntil(this.onDestroy$))
      .subscribe(
        {
          next: r => { this.carbodies = r; },
          error: err => { console.log("error", err); }
        });

    this.getFiltersService.getFuels()
      .pipe(takeUntil(this.onDestroy$))
      .subscribe(
        {
          next: r => { this.fuels = r; },
          error: err => { console.log("error", err); }
        });

    this.getFiltersService.getTransmissionTypes()
      .pipe(takeUntil(this.onDestroy$))
      .subscribe(
        {
          next: r => { this.transmissionTypes = r; },
          error: err => { console.log("error", err); }
        }),

      this.getFiltersService.getModels()
        .pipe(takeUntil(this.onDestroy$))
        .subscribe(
          {
            next: r => { this.models = r; },
            error: err => { console.log("error", err); }
          }),

      this.getFiltersService.getBrands()
        .pipe(takeUntil(this.onDestroy$))
        .subscribe(
          {
            next: r => { this.brands = r; },
            error: err => { console.log("error", err); }
          })

      this.getFiltersService.getYears()
        .pipe(takeUntil(this.onDestroy$))
        .subscribe(
          {
            next: r => { this.years = r; },
            error: err => { console.log("error", err); }
          })
  }

  initForm() {
    this.addCarForm = this.formBuilder.group({
      brand: ['', Validators.required],
      model: ['', Validators.required],
      yearOfProduction: ['', Validators.required],
      mileage: ['', Validators.required],
      carbodyType: ['', Validators.required],
      transmissionType: ['', Validators.required],
      vehicleImage: [null, Validators.required]
    });
    this.addCarForm.addControl('fuelsOptions', new FormGroup({}));
    const fuelsTemp: FormGroup = this.addCarForm.controls.fuels as FormGroup;
    for (const fuel of this.fuels) {
      fuelsTemp.addControl(fuel.fuelName, new FormControl(false));
    }
  }
 
  fileChanged(event: Event) {
    const imageInputElement = event.target as HTMLInputElement;
    if (imageInputElement.files && imageInputElement.files.length > 0) {
      const files = imageInputElement.files;
      for (const file of files) {
        this.vehicleImages.push(file);
      }
    }
  }

  submitForm() {
    if (this.addCarForm.valid) {
      const formData: AddCarInterface = this.addCarForm.value;
      const newVehicle: Vehicle = new Vehicle();

      newVehicle.brandId = this.brands.find(brand => brand.brandName === formData.brand)!.brandId!;
      newVehicle.modelId = this.models.find(model => model.modelName === formData.model)!.modelId!;

      newVehicle.yearOfProductionId = this.years.find(year => year.year === formData.yearOfProduction)!.yearId!;
      newVehicle.mileage = formData.mileage;

      formData.fuels.forEach(f1 => {
        const matchingFuel = this.fuels.find(f2 => f2.fuelName === f1);
        if (matchingFuel) {
          newVehicle.fuels.push(matchingFuel);
        }
      })

      newVehicle.carbodyId = this.carbodies.find(c => c.carbodyName === formData.carBodyType)!.carbodyId!;

      for (const opElement of this.operationalEquipment) {
        if (opElement.isChecked === true) {
          const matchingEquipmentElement = this.equipment.find(ee => ee.elementId === opElement.eqElement.elementId);
          if (matchingEquipmentElement) {
            newVehicle.equipment.push(matchingEquipmentElement);
          }
        }
      }
      this.addCarService.addVehicle(newVehicle)
        .pipe(takeUntil(this.onDestroy$))
        .subscribe(
          {
            next: r => { debugger },
            error: err => { console.log("błąd"), console.log(err) }
          });
    }
    else {
      console.log("Form is not valid!!!");
      this.getFormValidationErrors();
    } 
  }

  getFormValidationErrors() {
    Object.keys(this.addCarForm.controls).forEach((key,value) => {
      const controlErrors: ValidationErrors = this.addCarForm.get(key)!.errors!;
      Object.keys(controlErrors || {}).forEach(keyError => {
        console.log(`Key control: ${key}, keyError: ${keyError}, errValue: ${controlErrors[keyError]}`);
      });
    });
  }

  collapse() {
    this.isExpanded = false;
  }

  toggle() {
    this.isExpanded = !this.isExpanded;
  }
}
<section class="mt-5 mb-5">
  <form [formGroup]="addCarForm" (ngSubmit)="submitForm()">
    <div class="container-fluid">
      <div class="row justify-content-center">
        <div class="col-8 mb-3">
          <label for="BrandDataList" class="form-label">Marka</label>
          <input formControlName="brand" class="form-control" list="datalistOptions" id="BrandDataList" placeholder="Marka">
          <datalist id="BrandDatalistOptions">
            <option *ngFor="let brand of brands" [value]="brand.brandName"></option>
          </datalist>
        </div>
        <div class="col-8 mb-3">
          <label for="ModelDataList" class="form-label">Model</label>
          <input formControlName="model" class="form-control" list="datalistOptions" id="ModelDataList" placeholder="Model">
          <datalist id="ModelDatalistOptions">
            <option *ngFor="let model of models" [value]="model.modelName"></option>
          </datalist>
        </div>
        <div class="col-8">
          <div class="input-group mb-3">
            <label for="exampleDataList" class="form-label w-100">Rok Produkcji</label>
            <input formControlName="yearOfProduction" type="text" class="form-control" placeholder="Rok produkcji" aria-label="Rok produkcji" aria-describedby="basic-addon1">
          </div>
        </div>
        <div class="col-8">
          <div class="input-group mb-3">
            <label for="exampleDataList" class="form-label w-100">Przebieg</label>
            <input formControlName="mileage" type="text" class="form-control" placeholder="Przebieg" aria-label="Przebieg" aria-describedby="basic-addon1">
          </div>
        </div>
        <div class="col-8 mb-3 position-relative">
          <label for="customSelect" class="form-label">Rodzaj paliwa</label>
          <div class="customSelect" id="customSelect" (click)="displaySelectOptions()" tabindex="0">Wybierz rodzaje paliwa</div>
          <div class="options position-absolute w-auto" [ngClass]="{'displayOptions': optionsExpanded}" formGroupName="fuelsOptions">
            <div class="option" *ngFor="let fuel of fuels">
              <input type="checkbox" id="{{fuel.fuelName}}" name="{{fuel.fuelName}}" formControlName="{{fuel.fuelName}}"/>
              <label class="label-box" for="{{fuel.fuelName}}">{{fuel.fuelName}}</label>
            </div>
          </div>
        </div>
        <div class="col-8">
          <div class="input-group mb-3">
            <label for="exampleDataList" class="form-label w-100">Liczba drzwi</label>
            <input formControlName="doorsAmount" type="text" class="form-control" placeholder="Liczba drzwi" aria-label="Liczba drzwi" aria-describedby="basic-addon1">
          </div>
        </div>
        <div class="col-8">
          <div class="mb-3">
            <label for="TransmissionTypeDataList" class="form-label">Rodzaj skrzyni biegów</label>
            <input formControlName="transmissionType" class="form-control" list="datalistOptions" id="TransmissionTypeDataList" placeholder="Rodzaj skrzyni biegów">
            <datalist id="TransmissionTypeDatalistOptions">
              <option *ngFor="let transmissionType of transmissionTypes" [value]="transmissionType.typeName"></option>
            </datalist>
          </div>
        </div>
        <div class="col-8">
          <div class="mb-3">
            <label for="CarbodyDataList" class="form-label">Rodzaj nadwozia</label>
            <input formControlName="carbodyType" class="form-control" list="datalistOptions" id="CarbodyDataList" placeholder="Rodzaj nadwozia">
            <datalist id="CarbodyDatalistOptions">
              <option *ngFor="let carbody of carbodies" [value]="carbody.carbodyName"></option>
            </datalist>
          </div>
        </div>
        <div class="col-8">
          <div class="mb-3">
            <label for="vehicleImage" class="form-label">Zdjęcie pojazdu</label>
            <input formControlName="vehicleImage" class="form-control" id="vehicleImage" type="file" (change)="fileChanged($event)" multiple>
          </div>
        </div>
        <div class="col-8 mb-5">
          <div class="row">
            <div class="col-4 mb-2 d-flex justify-content-center" *ngFor="let opElement of operationalEquipment">
              <input type="checkbox" class="btn-check" id="{{opElement.eqElement.elementName}}" autocomplete="off" (click)="updateValue(opElement)">
              <label class="btn btn-outline-primary" for="{{opElement.eqElement.elementName}}">{{opElement.eqElement.elementName}}</label>
            </div>
          </div>
        </div>
        <div class="col-8 d-flex justify-content-center">
          <button type="submit" class="btn btn-primary w-auto submitBtn">Dodaj pojazd</button>
        </div>
      </div>
    </div>
  </form>
</section>
