import { AbstractControl, Form, FormGroup, ValidationErrors, ValidatorFn } from '@angular/forms';

export function createAtLeastOneValidator(): ValidatorFn {
  return (form: AbstractControl): ValidationErrors | null => {

    const formGorup = form as FormGroup;
    const controlsArray = formGorup.controls;

    for (const [key, value] of Object.entries(controlsArray)) {
      console.log(`${key}: ${value}`);
    }
    
    return { atLeastOne: true };
  }
}
